mapper.insert:
INSERT INTO t_emotion

    iuser=#{iuser}
    , iemotion_grade=#{emoGrade}
    , iemotion_tag=#{emoTagInt}

mapper.delete:
DELETE FROM t_emotion
WHERE iuser=#{iuser}
AND iemotion=#{iemotion}

mapper.select:
SELECT iemotion_tag AS emoTagInt
FROM t_emotion_tag
WHERE emotion_tag=#{emoTag}

SELECT iuser AS checkIuser
FROM t_user
WHERE iuser=#{iuser}
